import React, { Component } from 'react'
import Keybord from './Keybord'
import './App.css';


class App extends Component {

    state = {
      wordColection: ["train", "code", "licorne", "glace", "sourie", "wordpresse"],
      currenWord: null,
      alphabet: "abcdefghijklmnopqrstuvwxyz".split(''),
      usedLetter: [],
      win: 0,
      attempts: 0,
    };


    clickLetter = (letter) => {
      console.log("=>" + letter)

        if(this.state.usedLetter.indexOf(letter) === -1){
          let attempts = this.state.attempts

          const usedLetter = [...this.state.usedLetter, letter]

          if(this.state.currenWord.indexOf(letter) === -1){
             attempts = this.state.attempts +1
          }

          let win = 1
          for(let i = 0; i < this.state.currenWord.length; i++){
              if(usedLetter.indexOf(this.state.currenWord[i]) === 1 ){
                win = -1
              }
          }

          if(attempts === 9 && win === -1)


          this.setState({usedLetter, attempts, win})
        }



    }


    componentDidMount() {
      this.initGame()
    }


    initGame = () => {
      this.setState({currenWord: 'licorne'})
    }


  render() {
     return (
       <div className="App">
         <h1>pendu</h1>
         <div>{this.state.currenWord}</div>
         <Keybord alphabet={this.state.alphabet}
                  action={this.clickLetter}
         />
       </div>
   )
  }
}

export default App;
